export class TimeConverter {
  public convertTimeSteampToBrazilianDateTime(start: number) {
    const jsonStr = `{"timezone": "BRST", "start": ${start}, "gmtoffset": -7200}`;

    // Converte a string JSON em um objeto JavaScript
    const data = JSON.parse(jsonStr);

    // Converte o timestamp em segundos para uma data JavaScript
    const startTime = new Date(data.start * 1000);

    // Define o fuso horário da B3 como America/Sao_Paulo
    const b3TimeZone = "America/Sao_Paulo";

    // Converte a hora de início e fechamento para o fuso horário da B3
    const b3StartTime = startTime.toLocaleString("pt-BR", {
      timeZone: b3TimeZone,
    });

    const b3StartTimeStr = new Date(b3StartTime).toLocaleString("pt-BR", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
    });

    console.log(start);
    console.log(`Horário de abertura da B3: ${b3StartTimeStr}`);
  }
}
