export class TimeConverter {
  public convertTimeSteampToBrazilianDateTime(start: number, end: number) {
    const date = new Date(start * 1000); // converte para milissegundos

    // Ajusta para o fuso horário de Brasília (UTC-03:00)
    date.setUTCHours(date.getUTCHours() - 3);

    // Obtém os valores de data e hora em horário de Brasília
    const brYear = date.getFullYear();
    const brMonth = date.getMonth() + 1; // adiciona 1 para exibir o mês correto
    const brDay = date.getDate();
    const brHours = date.getHours();
    const brMinutes = date.getMinutes();
    const brSeconds = date.getSeconds();

    // Formata a data e hora em uma string
    const formattedDate = `${brDay}/${brMonth}/${brYear} ${brHours}:${brMinutes}:${brSeconds}`;

    console.log(formattedDate); // exibe "10/06/2022 23:00:00"
  }
}
