import { Component, OnInit, ViewChild, ElementRef } from "@angular/core";
import { Chart } from "chart.js";
@Component({
  selector: "app-quote-chart",
  templateUrl: "./quote-chart.component.html",
  styleUrls: ["./quote-chart.component.scss"],
})
export class QuoteChartComponent {
  @ViewChild("meuCanvas", { static: true }) elemento = {} as ElementRef;

  ngOnInit() {
    new Chart(this.elemento.nativeElement, {
      type: "bubble",
      data: {
        labels: [
          "Janeiro",
          "Fevereiro",
          "Março",
          "Abril",
          "Maio",
          "Junho",
          "Julho",
          "Agosto",
          "Setembro",
          "Outubro",
          "Novembro",
          "Dezembro",
        ],
        datasets: [
          {
            data: [85, 72, 86, 81, 84, 86, 94, 60, 62, 65, 41, 58],
            borderColor: "#00AEFF",
            fill: false,
          },
          {
            data: [33, 38, 10, 93, 68, 50, 35, 29, 34, 2, 62, 4],
            borderColor: "#FFCC00",
            fill: false,
          },
        ],
      },
      options: {},
    });
  }
}
<div>
  <canvas #meuCanvas></canvas>
</div>
