import { Injectable } from "@angular/core";
import { ChartDto } from "src/models/chartDto";
import { Interval } from "src/models/interval.enum";
import { Range } from "src/models/range.enum";
import { ProxyService } from "../proxy/proxy.service";
@Injectable({
  providedIn: "root",
})
export class AdapterSetvice {
  constructor(private proxyService: ProxyService) {}
  public getStockValues(stock: string, interval: Interval, range: Range): any {
    return this.proxyService
      .consultStockVaration(stock, interval, range)
      .subscribe((res: any) => {
        return this.converterchartServiceToChartDto(res);
      });
  }
  public converterchartServiceToChartDto(data: any): ChartDto {
    const chartDto = new ChartDto(
      data.chart.result[0].indicators.quote[0].open,
      data.chart.result[0].timestamp
    );
    return chartDto;
  }
}
