import { Injectable } from "@angular/core";
import { ChartDto } from "src/models/chartDto";
import { Interval } from "src/models/interval.enum";
import { Range } from "src/models/range.enum";
import { ProxyService } from "../proxy/proxy.service";
@Injectable({
  providedIn: "root",
})
export class AdapterSetvice {
  private data: any;
  constructor(private proxyService: ProxyService) {}
  public async getStockValues(stock: string, interval: Interval, range: Range) {
    await this.proxyService
      .consultStockVaration(stock, interval, range)
      .subscribe(async (res: any) => {
        this.data = res;
      });
    return this.converterchartServiceToChartDto();
  }
  public converterchartServiceToChartDto(): ChartDto {
    const chartDto = new ChartDto(
      this.data.chart.result[0].indicators.quote[0].open,
      this.data.chart.result[0].timestamp
    );
    return chartDto;
  }
}
